import React, { useState }  from "react";
import { Container, Paper, MantineProvider } from '@mantine/core';
import { Calendar } from '@mantine/dates';

function LargeCalendar() {
    return (
        <Calendar
        className="custom-calendar"
        styles={{
            root: { border: '1px solid red' },
            wrapper: { background: 'lightgray' },
            weekday: { color: 'blue' },
            date: { background: 'lightblue' },
            selected: { background: 'blue' },
        }}
        size="lg"
        colorScheme="dark"
        shadow="md"
        />
    );
}

export default LargeCalendar;
