import React from 'react';
import { Link } from 'react-router-dom';
import NavBar from '../components/NavBar';

function CurrentReservations(){

    function toggleBar(button) {
        // Remove 'active' class from all buttons
        const buttons = document.querySelectorAll('.nav-button');
        buttons.forEach(btn => btn.classList.remove('active'));

        // Add 'active' class to the clicked button
        button.classList.add('active');

        // Move the sliding bar to the selected button
        const slidingBar = document.querySelector('.sliding-bar');
        slidingBar.style.width = button.offsetWidth + 'px';
        slidingBar.style.transform = `translateX(${button.offsetLeft}px)`;
        slidingBar.style.backgroundColor = 'blue'; /* Change bar color here */
      }

    return(
        <div id="reservations-page">
            <link rel="preconnect" href="https://fonts.googleapis.com"/>
            <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin/>
            <link href="https://fonts.googleapis.com/css2?family=Gabarito:wght@400;700&display=swap" rel="stylesheet"></link>
            <NavBar />
            <nav id="reservations-navbar">
                <button id="pending-reservations-button" onClick={toggleBar(this)}>Pending</button>
                <button id="approved-reservations-button">Approved</button>
                <button id="denied-reservations-button">Denied</button>
                <div id="sliding-bar"></div>
            </nav>
            <div id="reservations-list-div">
            </div>
        </div>
    );
}

export default CurrentReservations;
