import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useSpring, animated } from 'react-spring';
import NavBar from '../components/NavBar';
import '../'

function CurrentReservations(){

    const [selectedOption, setSelectedOption] = useState(null);

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    const options = ['pending', 'approved', 'denied'];
    const selectedOptionIndex = options.indexOf(selectedOption);
    const sliderPosition = (selectedOptionIndex / (options.length - 1)) * 100;

    const sliderProps = useSpring({
        width: `${sliderPosition}%`,
        backgroundColor: selectedOption ? '#000' : '#fff',
    });


    return(
        <div id="reservations-page">
            <link rel="preconnect" href="https://fonts.googleapis.com"/>
            <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin/>
            <link href="https://fonts.googleapis.com/css2?family=Gabarito:wght@400;700&display=swap" rel="stylesheet"></link>
            <NavBar />
            <nav id="reservations-navbar">
                <label>
                    <input type="radio" value="pending" checked={selectedOption === 'pending'} onChange={handleOptionChange}/>
                    Pending
                </label>
                <label>
                    <input type="radio" value="approved" checked={selectedOption === 'approved'} onChange={handleOptionChange}/>
                    Approved
                </label>
                <label>
                    <input type="radio" value="denied" checked={selectedOption === 'denied'} onChange={handleOptionChange}/>
                    Denied
                </label>
                <div className="reservation-bar">
                    <animated.div className="reservation-sliding-bar" style={sliderProps}></animated.div>
                </div>
            </nav>
            <div id="reservations-list-div">
            </div>
        </div>
    );
}

export default CurrentReservations;
