import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useSpring, animated } from 'react-spring';
import NavBar from '../components/NavBar';

function CurrentReservations(){

    const [selectedButton, setSelectedButton] = useState(0);
    const buttonRefs = useRef([]);
    const buttonCount = 3;
    let initialTransform = 0;

    useEffect(() => {
        let totalWidth = 0;
        for (let i = 0; i < buttonCount; i++) {
          if (buttonRefs.current[i]) {
            totalWidth += buttonRefs.current[i].offsetWidth;
          }
        }
        setTotalButtonWidth(totalWidth);
      }, [buttonCount]);

    const handleButtonClick = (index) => {
        setSelectedButton(index);
    };

    const [totalButtonWidth, setTotalButtonWidth] = useState(0);
    const buttonWidth = totalButtonWidth / buttonCount;

    const calculateInitialTransform = () => {
        let totalTransform = 0;

        // Calculate the total width of all buttons
        const totalWidthOfButtons = buttonRefs.current.reduce((total, button) => {
          return total + (button ? button.offsetWidth : 0);
        }, 0);

        // Calculate the width of each button
        const buttonWidth = totalWidthOfButtons / buttonRefs.current.length;

        // Calculate the initial transform based on the selected button
        for (let i = 0; i < selectedButton; i++) {
          if (buttonRefs.current[i] == 1) {
            totalTransform += buttonWidth + 47;
          }
          else if (buttonRefs.current[i] == 2)
          {
            totalTransform += button
          }
        }

        return totalTransform;
    };

    const slidingBarStyle = useSpring({
        width: `${buttonWidth}px`,
        transform: `translateX(${calculateInitialTransform()}px)`
    });

    return(
        <div id="reservations-page">
            <link rel="preconnect" href="https://fonts.googleapis.com"/>
            <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin/>
            <link href="https://fonts.googleapis.com/css2?family=Gabarito:wght@400;700&display=swap" rel="stylesheet"></link>
            <NavBar />
            <nav id="reservations-navbar">
                <button ref={el => (buttonRefs.current[0] = el)} className={`reservation-button ${selectedButton === 0 ? 'active' : ''}`} onClick={() => handleButtonClick(0)}>Pending</button>
                <button ref={el => (buttonRefs.current[1] = el)} className={`reservation-button ${selectedButton === 1 ? 'active' : ''}`} onClick={() => handleButtonClick(1)}>Approved</button>
                <button ref={el => (buttonRefs.current[2] = el)} className={`reservation-button ${selectedButton === 2 ? 'active' : ''}`} onClick={() => handleButtonClick(2)}>Denied</button>
                <animated.div className="sliding-bar" style={{ ...slidingBarStyle }}></animated.div>
            </nav>
            <div id="reservations-list-div">
            </div>
        </div>
    );
}

export default CurrentReservations;
